mapper.select:
SELECT a.id, a.title, a.sub_title AS subTitle, a.level, a.version, a.create_date AS createDate, b.nickname, c.name AS category, d.name AS auth FROM t_wiki AS a LEFT JOIN t_user_message AS b ON a.create_by = b.user_id LEFT JOIN t_wiki_category AS c ON a.category - a.category % 10 = c.id LEFT JOIN t_wiki_category AS d ON a.category % 10 = d.id WHERE 1 = 1
AND a.id = #{id, jdbcType=INTEGER}
AND a.title LIKE CONCAT('%',#{title, jdbcType=VARCHAR},'%')
AND a.sub_title LIKE CONCAT('%',#{subTitle, jdbcType=VARCHAR},'%')
AND a.level = #{level, jdbcType=INTEGER}
AND a.create_by = #{createBy, jdbcType=INTEGER}
AND b.nickname LIKE CONCAT('%',#{nickname, jdbcType=VARCHAR},'%')
AND a.category = #{category, jdbcType=INTEGER}
LIMIT #{startIndex, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}

SELECT COUNT(*) FROM t_wiki AS a
LEFT JOIN t_user_message AS b
ON a.create_by = b.user_id
WHERE 1=1

	AND a.id = #{id, jdbcType=INTEGER}


	AND a.title LIKE CONCAT('%',#{title, jdbcType=VARCHAR},'%')


	AND a.sub_title LIKE CONCAT('%',#{subTitle, jdbcType=VARCHAR},'%')


	AND a.level = #{level, jdbcType=INTEGER}


	AND a.create_by = #{createBy, jdbcType=INTEGER}


	AND b.nickname LIKE CONCAT('%',#{nickname, jdbcType=VARCHAR},'%')


	AND a.category = #{category, jdbcType=INTEGER}
