mapper.select:
SELECT resources_id,resources_name,resources_url FROM t_resources WHERE 1=1
AND resources_id = #{id, jdbcType=INTEGER}
AND resources_name LIKE CONCAT('%',#{name, jdbcType=VARCHAR},'%')
AND resources_url LIKE CONCAT('%',#{url, jdbcType=VARCHAR},'%')
AND resources_id IN (SELECT resources_id FROM t_resources_authorities WHERE authority_id = #{authId, jdbcType=INTEGER})
LIMIT #{startIndex, jdbcType=INTEGER},#{pageSize, jdbcType=INTEGER}

mapper.delete:
DELETE FROM t_resources_authorities
WHERE resources_id = #{resourceId, jdbcType=INTEGER}
AND authority_id = #{authId, jdbcType=INTEGER}
